mapper.select:
SELECT count(*) as cnt 
FROM form_list
WHERE user_seq = #{user_seq}

SELECT * 
FROM form_list
WHERE user_seq = #{user_seq}
ORDER BY is_main DESC
LIMIT #{paging.startRow}, #{paging.rowSize}

SELECT * 
FROM form_list
WHERE 1=1
AND user_seq = #{user_seq}
AND form_seq = #{form_seq}

mapper.update:
UPDATE form_list
SET form_title = #{form_title}
WHERE 1=1
AND `user_seq` = #{user_seq}
AND `form_seq` = #{form_seq}

mapper.delete:
    	DELETE FROM form_list
    	WHERE 1=1
    	AND `user_seq` = #{user_seq}
    	AND `form_seq` IN 
    	
	        #{code}
